def get_icon(name: str, size: int = 24, color: str = "currentColor") -> str:
    """Get an SVG icon by name with optional size and color."""
    icon = ICONS.get(name, ICONS["dashboard"])
    
    # Replace size
    icon = icon.replace('width="24"', f'width="{size}"')
    icon = icon.replace('height="24"', f'height="{size}"')
    
    # Replace color if specified
    if color != "currentColor":
        icon = icon.replace('stroke="currentColor"', f'stroke="{color}"')
    
    return icon


def icon_html(name: str, size: int = 24, color: str = "currentColor", 
              style: str = "") -> str:
    """Get icon wrapped in a styled span for inline use."""
    icon = get_icon(name, size, color)
    return f'<span style="display:inline-flex;align-items:center;{style}">{icon}</span>'


